		if vBegin ~= nil and vEnd ~= nil then
			for i= vBegin, vEnd, 1 do
				local tmod = template;
				local s = tostring(i);
				local c;

				tmod, c = string.gsub(tmod,"##IDX##", s)
				res = res..tmod;
				res = res..','
			end
		end
	end
end
if removeLast == true then
	res = res:sub(1, -2)
end

